import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

import SudokuSolver from './sudokusolver.js';

class Square extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            value: 0,
            row: this.props.row,
            col: this.props.col
        };

        this.fillSquare = this.fillSquare.bind(this);
    };

    fillSquare() {
        var number;
        var userInput = prompt("Enter a number.");

        try {
            number = parseInt(userInput);
            if(number > 9 || number < 1) {
                alert("Please enter an integer from 1 to 9.");
            } else {
                this.setState({value: number});
            };
        } catch {
            alert("Please enter an integer from 1 to 9.");
        };
    };

    render() {
        return (
            <button className="square"
                    onClick={ this.fillSquare }
            >
                {(this.state.value > 0 ? this.state.value : "")}
            </button>
        );
    };
}

class Board extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            board: Array(81).fill(0)
        };
    };

    renderSquare(i, j) {
        return <Square value={this.state.board[i][j]} />;
    };

    solveBoard() {

    };

    render() {
        //9x9 sudoku board
        const status = 'Please enter the starting numbers.';

        return (
            <div>
                <div className="status">{status}</div>
                <div className="board-row">
                    {this.renderSquare(0, 0)}
                    {this.renderSquare(0, 1)}
                    {this.renderSquare(0, 2)}
                    {this.renderSquare(0, 3)}
                    {this.renderSquare(0, 4)}
                    {this.renderSquare(0, 5)}
                    {this.renderSquare(0, 6)}
                    {this.renderSquare(0, 7)}
                    {this.renderSquare(0, 8)}
                </div>
                <div className="board-row">
                    {this.renderSquare(1, 0)}
                    {this.renderSquare(1, 1)}
                    {this.renderSquare(1, 2)}
                    {this.renderSquare(1, 3)}
                    {this.renderSquare(1, 4)}
                    {this.renderSquare(1, 5)}
                    {this.renderSquare(1, 6)}
                    {this.renderSquare(1, 7)}
                    {this.renderSquare(1, 8)}
                </div>
                <div className="board-row">
                    {this.renderSquare(2, 0)}
                    {this.renderSquare(2, 1)}
                    {this.renderSquare(2, 2)}
                    {this.renderSquare(2, 3)}
                    {this.renderSquare(2, 4)}
                    {this.renderSquare(2, 5)}
                    {this.renderSquare(2, 6)}
                    {this.renderSquare(2, 7)}
                    {this.renderSquare(2, 8)}
                </div>
                <div className="board-row">
                    {this.renderSquare(3, 0)}
                    {this.renderSquare(3, 1)}
                    {this.renderSquare(3, 2)}
                    {this.renderSquare(3, 3)}
                    {this.renderSquare(3, 4)}
                    {this.renderSquare(3, 5)}
                    {this.renderSquare(3, 6)}
                    {this.renderSquare(3, 7)}
                    {this.renderSquare(3, 8)}
                </div>
                <div className="board-row">
                    {this.renderSquare(4, 0)}
                    {this.renderSquare(4, 1)}
                    {this.renderSquare(4, 2)}
                    {this.renderSquare(4, 3)}
                    {this.renderSquare(4, 4)}
                    {this.renderSquare(4, 5)}
                    {this.renderSquare(4, 6)}
                    {this.renderSquare(4, 7)}
                    {this.renderSquare(4, 8)}
                </div>
                <div className="board-row">
                    {this.renderSquare(5, 0)}
                    {this.renderSquare(5, 1)}
                    {this.renderSquare(5, 2)}
                    {this.renderSquare(5, 3)}
                    {this.renderSquare(5, 4)}
                    {this.renderSquare(5, 5)}
                    {this.renderSquare(5, 6)}
